var expect=require("expect.js"),cheerio=require("../.."),forms=require("../fixtures").forms;describe("$(...)",function(){var a;beforeEach(function(){a=cheerio.load(forms)}),describe(".serializeArray",function(){it("() : should get form controls",function(){expect(a("form#simple").serializeArray()).to.eql([{name:"fruit",value:"Apple"}])}),it("() : should get nested form controls",function(){expect(a("form#nested").serializeArray()).to.have.length(2);var b=a("form#nested").serializeArray();b.sort(function(a,b){return a.value-b.value}),expect(b).to.eql([{name:"fruit",value:"Apple"},{name:"vegetable",value:"Carrot"}])}),it("() : should not get disabled form controls",function(){expect(a("form#disabled").serializeArray()).to.eql([])}),it("() : should not get form controls with the wrong type",function(){expect(a("form#submit").serializeArray()).to.eql([{name:"fruit",value:"Apple"}])}),it("() : should get selected options",function(){expect(a("form#select").serializeArray()).to.eql([{name:"fruit",value:"Orange"}])}),it("() : should not get unnamed form controls",function(){expect(a("form#unnamed").serializeArray()).to.eql([{name:"fruit",value:"Apple"}])}),it("() : should get multiple selected options",function(){expect(a("form#multiple").serializeArray()).to.have.length(2);var b=a("form#multiple").serializeArray();b.sort(function(a,b){return a.value-b.value}),expect(b).to.eql([{name:"fruit",value:"Apple"},{name:"fruit",value:"Orange"}])}),it("() : should get individually selected elements",function(){var b=a("form#nested input").serializeArray();b.sort(function(a,b){return a.value-b.value}),expect(b).to.eql([{name:"fruit",value:"Apple"},{name:"vegetable",value:"Carrot"}])}),it("() : should standardize line breaks",function(){expect(a("form#textarea").serializeArray()).to.eql([{name:"fruits",value:"Apple\r\nOrange"}])})})});