var expect=require("expect.js"),_=require("lodash"),cheerio=require(".."),xml=function(a,b){b=_.extend({xmlMode:!0},b);var c=cheerio.load(a,b);return c.xml()},dom=function(a,b){var c=cheerio.load("",b);return c(a).html()};describe("render",function(){describe("(xml)",function(){it("should render <media:thumbnail /> tags correctly",function(){var a='<media:thumbnail url="http://www.foo.com/keyframe.jpg" width="75" height="50" time="12:05:01.123" />';expect(xml(a)).to.equal('<media:thumbnail url="http://www.foo.com/keyframe.jpg" width="75" height="50" time="12:05:01.123"/>')}),it("should render <link /> tags (RSS) correctly",function(){var a="<link>http://www.github.com/</link>";expect(xml(a)).to.equal("<link>http://www.github.com/</link>")}),it("should escape entities",function(){var a='<tag attr="foo &amp; bar"/>';expect(xml(a)).to.equal(a)})}),describe("(dom)",function(){it("should keep camelCase for new nodes",function(){var a='<g><someElem someAttribute="something">hello</someElem></g>';expect(dom(a,{xmlMode:!1})).to.equal('<someelem someattribute="something">hello</someelem>')}),it("should keep camelCase for new nodes",function(){var a='<g><someElem someAttribute="something">hello</someElem></g>';expect(dom(a,{xmlMode:!0})).to.equal('<someElem someAttribute="something">hello</someElem>')}),it("should maintain the parsing options of distinct contexts independently",function(){var a='<g><someElem someAttribute="something">hello</someElem></g>',b=cheerio.load("",{xmlMode:!1});expect(b(a).html()).to.equal('<someelem someattribute="something">hello</someelem>')})})});